mapper.update:
update ${prefix}ACT_RU_EXT_TASK
REV_ = #{revisionNext, jdbcType=INTEGER}, WORKER_ID_ = #{workerId, jdbcType=VARCHAR}, TOPIC_NAME_ = #{topicName, jdbcType=VARCHAR}, LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP}, RETRIES_ = #{retries, jdbcType=INTEGER}, ERROR_MSG_ = #{errorMessage, jdbcType=VARCHAR}, ERROR_DETAILS_ID_ = #{errorDetailsByteArrayId, jdbcType=VARCHAR}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, PROC_INST_ID_ = #{processInstanceId, jdbcType=VARCHAR}, PROC_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}, PROC_DEF_KEY_ = #{processDefinitionKey, jdbcType=VARCHAR}, ACT_ID_ = #{activityId, jdbcType=VARCHAR}, ACT_INST_ID_ = #{activityInstanceId, jdbcType=VARCHAR}, SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}, PRIORITY_ = #{priority, jdbcType=BIGINT}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_EXT_TASK

  SUSPENSION_STATE_ = #{parameter.suspensionState, jdbcType=INTEGER}



    PROC_INST_ID_ = #{parameter.processInstanceId}


    and PROC_DEF_ID_ = #{parameter.processDefinitionId}



      and PROC_DEF_KEY_ = #{parameter.processDefinitionKey, jdbcType=VARCHAR}


      and PROC_DEF_ID_ IN (
        SELECT ID_ 
        FROM ${prefix}ACT_RE_PROCDEF PD
        WHERE PD.KEY_ = #{parameter.processDefinitionKey, jdbcType=VARCHAR}

          and PD.TENANT_ID_ = #{parameter.processDefinitionTenantId, jdbcType=VARCHAR}


          and PD.TENANT_ID_ is null

      )

mapper.select:
${limitBefore} select ${distinct} RES.* ${limitBetween} from ${prefix}ACT_RU_EXT_TASK RES
AUTH ON ( (AUTH.RESOURCE_TYPE_ = 8 AND AUTH.RESOURCE_ID_ in (RES.PROC_INST_ID_, '*') AND ${bitand1}AUTH.PERMS_${bitand2}2${bitand3} = 2) OR (AUTH.RESOURCE_TYPE_ = 6 AND AUTH.RESOURCE_ID_ in (RES.PROC_DEF_KEY_, '*') AND ${bitand1}AUTH.PERMS_${bitand2}512${bitand3} = 512) )
AUTH1 ON ( (AUTH1.RESOURCE_TYPE_ = 8 AND AUTH1.RESOURCE_ID_ in (RES.PROC_INST_ID_, '*') AND ${bitand1}AUTH1.PERMS_${bitand2}4${bitand3} = 4) OR (AUTH1.RESOURCE_TYPE_ = 6 AND AUTH1.RESOURCE_ID_ in (RES.PROC_DEF_KEY_, '*') AND ${bitand1}AUTH1.PERMS_${bitand2}1024${bitand3} = 1024) )
(RES.LOCK_EXP_TIME_ is null or RES.LOCK_EXP_TIME_ <= #{parameter.now, jdbcType=TIMESTAMP}) and (RES.SUSPENSION_STATE_ is null or RES.SUSPENSION_STATE_ = 1) and (RES.RETRIES_ is null or RES.RETRIES_ > 0)
and RES.TOPIC_NAME_ in
#{topicName}
order by ${orderBy}
${limitAfter}

mapper.sql:
from ${prefix}ACT_RU_EXT_TASK RES



  AUTH ON (
   (AUTH.RESOURCE_TYPE_ = 8 
    AND AUTH.RESOURCE_ID_ in (RES.PROC_INST_ID_, '*') 
    AND ${bitand1}AUTH.PERMS_${bitand2}2${bitand3} = 2)
  OR
   (AUTH.RESOURCE_TYPE_ = 6 
    AND AUTH.RESOURCE_ID_ in (RES.PROC_DEF_KEY_, '*') 
    AND ${bitand1}AUTH.PERMS_${bitand2}512${bitand3} = 512) 
  )



    and RES.ID_ = #{externalTaskId}


    and RES.WORKER_ID_ = #{workerId}


    and RES.LOCK_EXP_TIME_ < #{lockExpirationBefore}


    and RES.LOCK_EXP_TIME_ > #{lockExpirationAfter}


    and RES.TOPIC_NAME_ = #{topicName}


    and RES.LOCK_EXP_TIME_ is not null
    and RES.LOCK_EXP_TIME_ > #{now, jdbcType=TIMESTAMP}


    and (RES.LOCK_EXP_TIME_ is null
    or RES.LOCK_EXP_TIME_ < #{now, jdbcType=TIMESTAMP})


    and RES.EXECUTION_ID_ = #{executionId}


    and RES.PROC_INST_ID_ = #{processInstanceId}


    and RES.PROC_DEF_ID_ = #{processDefinitionId}


    and RES.ACT_ID_ = #{activityId}


    and (RES.RETRIES_ is null
    or RES.RETRIES_ > 0)


    and RES.RETRIES_ is not null
    and RES.RETRIES_ = 0



        and RES.SUSPENSION_STATE_ = 1


        and RES.SUSPENSION_STATE_ = 2



    and RES.PRIORITY_ >= #{priorityHigherThanOrEquals}


    and RES.PRIORITY_ <= #{priorityLowerThanOrEquals}


    and RES.TENANT_ID_ in

      #{tenantId}
