mapper.update:
update ${prefix}ACT_HI_PROCINST set
  PROC_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR},
  PROC_DEF_KEY_ = #{processDefinitionKey, jdbcType=VARCHAR},
  END_ACT_ID_ = #{endActivityId, jdbcType=VARCHAR},
  DELETE_REASON_ = #{deleteReason, jdbcType=VARCHAR},
  STATE_ = #{state, jdbcType=VARCHAR}


    , END_TIME_ = #{endTime, jdbcType=TIMESTAMP}
    , DURATION_ = #{durationInMillis, jdbcType=BIGINT}


where ID_ = #{id, jdbcType=VARCHAR}

mapper.sql:
from ${prefix}ACT_HI_PROCINST RES


  LEFT JOIN (
    SELECT
        SELF.PROC_DEF_KEY_ AS KEY_
    FROM
        (SELECT DISTINCT DKEYS.PROC_DEF_KEY_ from ${prefix}ACT_HI_PROCINST DKEYS) SELF
    WHERE

  ) AUTH
  ON AUTH.KEY_ = RES.PROC_DEF_KEY_



  inner join ${prefix}ACT_RE_PROCDEF DEF on RES.PROC_DEF_ID_ = DEF.ID_



  inner join ${prefix}ACT_HI_INCIDENT INC on RES.PROC_INST_ID_ = INC.PROC_INST_ID_



    RES.PROC_INST_ID_ = #{processInstanceId}


    and RES.PROC_INST_ID_ in

      #{item}



    and RES.CASE_INST_ID_ = #{caseInstanceId}


    and RES.PROC_DEF_ID_ = #{processDefinitionId}


    and DEF.KEY_ = #{processDefinitionKey}


    and DEF.NAME_ = #{processDefinitionName}


    and DEF.NAME_ like #{processDefinitionNameLike}


    and RES.BUSINESS_KEY_ = #{businessKey}


    and RES.BUSINESS_KEY_ like #{businessKeyLike}



    and RES.START_TIME_ <= #{startedBefore}


    and RES.START_TIME_ >= #{startedAfter}


    and RES.END_TIME_ <= #{finishedBefore}


    and RES.END_TIME_ >= #{finishedAfter}



    and DEF.KEY_ not like #{procDefKey}





    and RES.START_TIME_ >= #{startDateOnBegin}
    and RES.START_TIME_ <= #{startDateOnEnd}



    and RES.END_TIME_ >= #{finishDateOnBegin}
    and RES.END_TIME_ <= #{finishDateOnEnd}



    and RES.END_TIME_ <= #{finishDateBy}



    and RES.START_TIME_ >= #{startDateBy}



    and RES.END_TIME_ IS NULL


    and RES.END_TIME_ is not NULL



    and INC.INCIDENT_MSG_ = #{incidentMessage}



    and INC.INCIDENT_MSG_ like #{incidentMessageLike}



    and RES.START_USER_ID_ = #{startedBy}


    and RES.SUPER_PROCESS_INSTANCE_ID_ = #{superProcessInstanceId}


    and RES.PROC_INST_ID_ = (select SUPER_PROCESS_INSTANCE_ID_ from ${prefix}ACT_HI_PROCINST where PROC_INST_ID_ = #{subProcessInstanceId})


    and RES.SUPER_CASE_INSTANCE_ID_ = #{superCaseInstanceId}


    and RES.PROC_INST_ID_ = (select SUPER_PROCESS_INSTANCE_ID_ from ${prefix}ACT_HI_CASEINST where CASE_INST_ID_ = #{subCaseInstanceId})


    and EXISTS (
      select
        ID_
      from
        ${prefix}ACT_HI_VARINST
      WHERE
        NAME_= #{queryVariableValue.name}
      AND PROC_INST_ID_ = RES.PROC_INST_ID_




      and


    )


    and RES.TENANT_ID_ in

      #{tenantId}




    and ((RES.PROC_DEF_KEY_ is not null and AUTH.KEY_ is not null) or RES.PROC_DEF_KEY_ is null)
