REV_ = #{revisionNext, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP}, LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR}, RETRIES_ = #{retries, jdbcType=INTEGER}, EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR}, EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR}, DUEDATE_ = #{duedate, jdbcType=TIMESTAMP}, SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}, PROCESS_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}, PROCESS_DEF_KEY_ = #{processDefinitionKey, jdbcType=VARCHAR}, JOB_DEF_ID_ = #{jobDefinitionId, jdbcType=VARCHAR}, DEPLOYMENT_ID_ = #{deploymentId, jdbcType=VARCHAR}, HANDLER_CFG_ = #{jobHandlerConfigurationRaw, jdbcType=VARCHAR}, PRIORITY_ = #{priority, jdbcType=BIGINT}, SEQUENCE_COUNTER_ = #{sequenceCounter, jdbcType=BIGINT}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_JOB
REV_ = #{revisionNext, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP}, LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR}, RETRIES_ = #{retries, jdbcType=INTEGER}, EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR}, EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR}, DUEDATE_ = #{duedate, jdbcType=TIMESTAMP}, SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}, PROCESS_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}, PROCESS_DEF_KEY_ = #{processDefinitionKey, jdbcType=VARCHAR}, JOB_DEF_ID_ = #{jobDefinitionId, jdbcType=VARCHAR}, DEPLOYMENT_ID_ = #{deploymentId, jdbcType=VARCHAR}, HANDLER_CFG_ = #{jobHandlerConfigurationRaw, jdbcType=VARCHAR}, PRIORITY_ = #{priority, jdbcType=BIGINT}, SEQUENCE_COUNTER_ = #{sequenceCounter, jdbcType=BIGINT}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}
