mapper.update:
update ${prefix}ACT_RU_JOB set
  REV_ = REV_ + 1,
  SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}


    ID_ = #{jobId, jdbcType=VARCHAR}


    and JOB_DEF_ID_ = #{jobDefinitionId, jdbcType=VARCHAR}


    and PROCESS_INSTANCE_ID_ = #{processInstanceId, jdbcType=VARCHAR}


    and PROCESS_DEF_ID_ = #{processDefinitionId, jdbcType=VARCHAR}


    and PROCESS_DEF_KEY_ = #{processDefinitionKey, jdbcType=VARCHAR}


    and HANDLER_TYPE_ =  #{handlerType, jdbcType=VARCHAR}

update ${prefix}ACT_RU_JOB set
  REV_ = REV_ + 1,
  RETRIES_ = #{retries, jdbcType=INTEGER}

  RETRIES_ = 0

    and JOB_DEF_ID_ = #{jobDefinitionId, jdbcType=VARCHAR}

mapper.select:
  	${limitBefore}
    select 
    	RES.* ${limitBetween}
    from ${prefix}ACT_RU_JOB RES

    where (RES.RETRIES_ > 0)
      and (RES.DUEDATE_ is null or RES.DUEDATE_ < #{parameter.now, jdbcType=TIMESTAMP})
      and (RES.LOCK_OWNER_ is null or RES.LOCK_EXP_TIME_ < #{parameter.now, jdbcType=TIMESTAMP})
      and (RES.SUSPENSION_STATE_ = 1)


        and (RES.DEPLOYMENT_ID_ is null 

          or 
          RES.DEPLOYMENT_ID_ in 

	          #{deploymentId}


        )

    ${limitAfter}

	${limitBefore}
  select RES.* ${limitBetween}
  from ${prefix}ACT_RU_JOB RES
  where (RETRIES_ > 0)
    and (DUEDATE_ is null or DUEDATE_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (EXCLUSIVE_ = TRUE)
    and (PROCESS_INSTANCE_ID_ = #{parameter.pid})
    and (RES.SUSPENSION_STATE_ = 1)
  ${limitAfter}

	${limitBefore}
  select RES.* ${limitBetween}
  from ${prefix}ACT_RU_JOB RES
  where (RETRIES_ > 0)
    and (DUEDATE_ is null or DUEDATE_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ < #{parameter.now, jdbcType=TIMESTAMP})
    and (EXCLUSIVE_ = 1)
    and (PROCESS_INSTANCE_ID_ = #{parameter.pid})  
    and (RES.SUSPENSION_STATE_ = 1)
  ${limitAfter}

select * from ${prefix}ACT_RU_JOB
where HANDLER_TYPE_ = #{parameter.handlerType}
and HANDLER_CFG_ =  #{parameter.handlerConfiguration}

mapper.sql:
from ${prefix}ACT_RU_JOB RES

    LEFT OUTER JOIN ${prefix}ACT_RU_EXECUTION PI ON PI.ID_ = RES.PROCESS_INSTANCE_ID_



    RES.ID_ = #{id}


    RES.JOB_DEF_ID_ = #{jobDefinitionId}


    and RES.PROCESS_INSTANCE_ID_ = #{processInstanceId}


    and RES.EXECUTION_ID_ = #{executionId}


    and RES.PROCESS_DEF_ID_ = #{processDefinitionId}


    and RES.PROCESS_DEF_KEY_ = #{processDefinitionKey}


    and RES.RETRIES_ > 0


    and (RES.RETRIES_ > 0)
    and (RES.DUEDATE_ is null or RES.DUEDATE_ <= #{now, jdbcType=TIMESTAMP})
    and (
        (RES.EXECUTION_ID_ is null)
        or 
        (PI.SUSPENSION_STATE_ = 1)     
  )


    and RES.TYPE_ = 'timer'


    and RES.TYPE_ = 'message'


    and RES.DUEDATE_ > #{duedateHigherThan}


    and RES.DUEDATE_ < #{duedateLowerThan}


    and RES.DUEDATE_ >= #{duedateHigherThanOrEqual}


    and RES.DUEDATE_ <= #{duedateLowerThanOrEqual}


  	and (RES.EXCEPTION_MSG_ is not null or RES.EXCEPTION_STACK_ID_ is not null)


  	and RES.EXCEPTION_MSG_ = #{exceptionMessage}


    and RES.RETRIES_ = 0


    and RES.SUSPENSION_STATE_ = #{suspensionState.stateCode}

mapper.select:
	 	${limitBefore}
	  select RES.* ${limitBetween} 
	  from ${prefix}ACT_RU_JOB RES
	  where (RETRIES_ > 0)
	    and (DUEDATE_ is null or DUEDATE_ <= #{parameter.now, jdbcType=TIMESTAMP})
	    and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ <= #{parameter.now, jdbcType=TIMESTAMP})
      and (RES.SUSPENSION_STATE_ = 1) 

        and (RES.DEPLOYMENT_ID_ is null 

          or 
          RES.DEPLOYMENT_ID_ in 

            #{deploymentId}


        )

	  ${limitAfter}

	${limitBefore}
  select RES.* ${limitBetween} 
  from ${prefix}ACT_RU_JOB RES
  where (RETRIES_ > 0)
    and (DUEDATE_ is null or DUEDATE_ <= #{parameter.now, jdbcType=TIMESTAMP})
    and (LOCK_OWNER_ is null or LOCK_EXP_TIME_ <= #{parameter.now, jdbcType=TIMESTAMP})
    and (EXCLUSIVE_ = TRUE)
    and (PROCESS_INSTANCE_ID_ = #{parameter.pid})
    and (RES.SUSPENSION_STATE_ = 1)
  ${limitAfter}

mapper.update:
update ${prefix}ACT_RU_JOB
REV_ = #{revisionNext, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP}, LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR}, RETRIES_ = #{retries, jdbcType=INTEGER}, EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR}, EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR}, DUEDATE_ = #{duedate, jdbcType=TIMESTAMP}, SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}

update ${prefix}ACT_RU_JOB
REV_ = #{revisionNext, jdbcType=INTEGER}, EXECUTION_ID_ = #{executionId, jdbcType=VARCHAR}, LOCK_EXP_TIME_ = #{lockExpirationTime, jdbcType=TIMESTAMP}, LOCK_OWNER_ = #{lockOwner, jdbcType=VARCHAR}, RETRIES_ = #{retries, jdbcType=INTEGER}, EXCEPTION_STACK_ID_ = #{exceptionByteArrayId, jdbcType=VARCHAR}, EXCEPTION_MSG_ = #{exceptionMessage, jdbcType=VARCHAR}, SUSPENSION_STATE_ = #{suspensionState, jdbcType=INTEGER}
where ID_= #{id, jdbcType=VARCHAR} and REV_ = #{revision, jdbcType=INTEGER}
