mapper.select:
select * from ${prefix}ACT_GE_BYTEARRAY 
where DEPLOYMENT_ID_ = #{deploymentId}
      AND NAME_ = #{resourceName}

select * from ${prefix}ACT_GE_BYTEARRAY 
where DEPLOYMENT_ID_ = #{deploymentId}
      AND ID_ = #{resourceId}

mapper.sql:
select B.* from ${prefix}ACT_GE_BYTEARRAY B inner join (select B.NAME_, MAX(D.DEPLOY_TIME_) DEPLOY_TIME_ from ${prefix}ACT_GE_BYTEARRAY B inner join ${prefix}ACT_RE_DEPLOYMENT D on B.DEPLOYMENT_ID_ = D.ID_ where D.NAME_ = #{parameter.deploymentName}
and B.NAME_ in
#{resourceName}
group by B.NAME_) LAST_RESOURCE
on B.NAME_ = LAST_RESOURCE.NAME_ inner join ${prefix}ACT_RE_DEPLOYMENT D on B.DEPLOYMENT_ID_ = D.ID_ and D.DEPLOY_TIME_ = LAST_RESOURCE.DEPLOY_TIME_ and D.NAME_ = #{parameter.deploymentName}

